### python 207_rete_input_fasta_formatter.py [-h] genome_multi_seq.fasta

from __future__ import division

import argparse
parser = argparse.ArgumentParser(
	description="rete_input_fasta_formatter takes the reference genome and splits into individual fasta files ready for analysis with RetroTector. This includes performing 15kb padding with random sequence when necessary.",
	epilog="Author: Andrew Mason; Release: 03/11/14; Contact: andrew.mason@roslin.ed.ac.uk")
parser.add_argument("ref_seq", help="Specify the reference multi sequence fastA file")
usr_args = parser.parse_args()

import sys
import re
sys.path.append("/groups2/avian_genomes/chicken_erv/04_avian_lineage_LTR_retrotransposon_full_analysis/00_pipeline")
import static_functions
import os
import shutil

# process sequence file
ref_file = open(usr_args.ref_seq).read().rstrip("\n")
headers = static_functions.header_extractor(ref_file)
seq_list = static_functions.seq_only_extractor(ref_file)

# create sequence files, padding if the sequence length is less than 30kb
padding = ""
i = 0
all_headers = []
for seq in seq_list:
	if (len(seq) < 30000):
		real_len = "RL-" + (str(float(len(seq) / 1000))) + "kb"
		name = ((headers[i]) + "_15k-padded_" + real_len + ".fasta")
		all_headers += [name]
		padded_seq = padding + seq + padding
		query = open(name, "w")
		query.write(">" + (headers[i]) + "_15k-padded_" + real_len + "\n" + padded_seq + "\n::")    # to create sequences files ready for ReTe
		query.close()
		i+=1
		
	else:	
		name = ((headers[i]) + ".fasta")
		all_headers += [name]
		query = open(name, "w")
		query.write(">" + (headers[i]) + "\n" + seq + "\n::")    # to create sequences files ready for ReTe
		query.close()
		i+=1

os.mkdir("NewDNA")
seq_names_file = open("sequence_names.txt", "w")
for filename in all_headers:
	shutil.move(filename, "NewDNA/")
	seq_names_file.write(filename + "\tC:\\Retrotector\\Workplace\\NewDNA\\" + filename + "\t" + filename.replace(".fasta", "") + "\n")
seq_names_file.close()
